import React, { useState, useEffect } from "react";
import QAService from "../services/QA-service";
const QA = () => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  console.log(messages);
  useEffect(() => {
    if (messages === "") {
      return;
    } else {
      QAService.getMessages().then((data) => {
        setMessages([data]);
      });
    }
  }, []);
  const handlePostMessage = () => {
    QAService.postMessage(newMessage).then((data) => {
      setMessages([...messages, data]);
      setNewMessage("");
    });
  };
  return (
    <div>
      <section style={{ display: "flex", justifyContent: "flex-end" }}>
        <input className="QASearch" type="text" placeholder="問題搜尋" />
        <button className="QASearch">
          <i className="fa-solid fa-magnifying-glass"></i>
        </button>
      </section>
      <section className="QABlock">
        <div className="QABorder">
          <div className="QANav">
            <a href="/QA">全部</a>
            <a href="/QA">追蹤</a>
            <a href="/QA">熱門</a>
          </div>
          <hr />
          <div className="QA">
            <div>
              <input
                type="text"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
              />
              <button onClick={handlePostMessage}>提交</button>{" "}
              <ul>
                {messages.map((message, index) => (
                  <li key={index}>
                    <p>{message.data.content}</p>
                    <p>{new Date(message.data.time).toLocaleString()}</p>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default QA;
