import React, { useState } from "react";

const Song = () => {
  const [isReading, setisReading] = useState(null);
  //假資料
  const videoList = [];

  return (
    <div className="container">
      <section className="videoListBlock">
        此處是所有的音樂影片
        <iframe
          width="100%"
          height="100%"
          id="your_iframe_id"
          src="https://www.youtube.com/embed/your_youtube_id?enablejsapi=1"
          title="YouTube video player"
          frameborder="0"
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
          allowfullscreen
          onload="onYouTubeIframeAPIReady()"
        ></iframe>
      </section>
      {isReading && (
        <section className="videoDetailsBlock">包含歌詞、歌手資訊等</section>
      )}
    </div>
  );
};

export default Song;
