import React, { useState, useEffect, useRef } from "react";
import QAService from "../services/QA-service";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
const QA = () => {
  const [value, setValue] = React.useState("");
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState({ title: "", content: "" });
  const [isPosting, setIsPosting] = useState(false);
  const [isReading, setIsReading] = useState(null);
  const [readMessage, setReadMessage] = useState([]);
  //首次render所有留言
  useEffect(() => {
    QAService.getMessages()
      .then((data) => {
        if (data && data.length !== 0) {
          setMessages(data);
        }
      })
      .catch((e) => {
        console.log(e);
      });
  }, []);
  //處理發布留言
  const handlePostMessage = () => {
    QAService.postMessage(newMessage.title, newMessage.content).then((data) => {
      setMessages([...messages, data]);
      setNewMessage({ title: "", content: "" });
      setIsPosting(!isPosting);
    });
  };
  //處理時間顯示
  function timeAgo(dateString) {
    const date = new Date(dateString);
    const now = new Date();
    const secondsPast = (now.getTime() - date.getTime()) / 1000;
    switch (true) {
      case secondsPast < 60:
        return "幾秒前";
      case secondsPast < 3600:
        return parseInt(secondsPast / 60) + " 分鐘";
      case secondsPast <= 86400:
        return parseInt(secondsPast / 3600) + " 小時";
      case secondsPast < 86400 * 7:
        return parseInt(secondsPast / 86400) + " 天";
      default:
        let year = date.getFullYear();
        let month = date.getMonth() + 1;
        let day = date.getDate();
        return year + "年" + month + "月" + day + "日";
    }
  }
  //處理輸入框外觀
  const textareaRef = useRef(null);
  useEffect(() => {
    if (textareaRef.current) {
      textareaRef.current.style.height = "auto";
      textareaRef.current.style.height = `${textareaRef.current.scrollHeight}px`;
    }
  }, [value, newMessage]);
  //處理點擊留言
  const handleClick = (_id) => {
    let { id } = useParams();

    QAService.getURLMessage(_id).then((data) => {
      setReadMessage(data);
      console.log(data);
      setIsReading(true);
    });
    return;
  };
  return (
    <div>
      <section className="QASearch">
        <textarea className="QASearch" placeholder="問題搜尋" />
        <button className="QASearch">
          <i className="fa-solid fa-magnifying-glass"></i>
        </button>
      </section>
      <section className="QABlock">
        <div className="QABorder">
          <div className="QANav">
            <a href="/QA">全部</a>
            <a href="/QA">追蹤</a>
            <a href="/QA">熱門</a>
          </div>
          <hr />
          <div className="post">
            <button onClick={() => setIsPosting(!isPosting)}>
              有什麼想問嗎？ 立即發問<span> </span>
              <i className="fa-solid fa-pen"></i>
            </button>
          </div>
          <div className="postBlock">
            {isPosting && (
              <div className="QAPost">
                <button
                  className="xMark"
                  onClick={() => setIsPosting(!isPosting)}
                >
                  <i className="fa-solid fa-xmark fa-2xl"></i>
                </button>
                <textarea
                  className="title"
                  type="text"
                  placeholder="標題"
                  value={newMessage.title}
                  onChange={(e) =>
                    setNewMessage({ ...newMessage, title: e.target.value })
                  }
                />
                <textarea
                  className="content"
                  type="text"
                  placeholder="內容"
                  value={newMessage.content}
                  onChange={(e) =>
                    setNewMessage({ ...newMessage, content: e.target.value })
                  }
                />
                <button className="post" onClick={handlePostMessage}>
                  發布
                </button>{" "}
              </div>
            )}
          </div>
          {isReading && (
            <Route path="/message/:id">
              <div className="DBlock">
                <div className="QAPost">
                  <button
                    className="xMark"
                    onClick={() => {
                      setIsReading("");
                      setReadMessage([]);
                    }}
                  >
                    <i className="fa-solid fa-xmark fa-2xl"></i>
                  </button>
                  <div className="QAPoster">
                    {readMessage.user.nickname
                      ? readMessage.user.nickname
                      : readMessage.user.username}
                  </div>
                  <div
                    className="QATimer"
                    title={new Date(readMessage.time).toLocaleString("zh-TW", {
                      hour12: true,
                      year: "numeric",
                      month: "numeric",
                      day: "numeric",
                      hour: "numeric",
                      minute: "numeric",
                      weekday: "long",
                    })}
                  >
                    {timeAgo(readMessage.time)}
                  </div>
                  <div className="QATitle">{readMessage.title}</div>
                  <div className="QAContent">{readMessage.content}</div>
                </div>
              </div>
            </Route>
          )}
          <div className="QA">
            <div>
              <ul>
                {messages.map((message, index) => (
                  <li
                    key={message._id}
                    onClick={() => handleClick(message._id)}
                  >
                    <div className="QAPoster">
                      {message.user.nickname
                        ? message.user.nickname
                        : message.user.username}
                    </div>{" "}
                    <div
                      className="QATimer"
                      title={new Date(message.time).toLocaleString("zh-TW", {
                        hour12: true,
                        year: "numeric",
                        month: "numeric",
                        day: "numeric",
                        hour: "numeric",
                        minute: "numeric",
                        weekday: "long",
                      })}
                    >
                      {timeAgo(message.time)}
                    </div>
                    <div className="QATitle">{message.title}</div>
                    <div className="QAContent">{message.content}</div>
                    <hr />
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default QA;
