import React, { useState } from "react";

const Song = () => {
  const [isReading, setisReading] = useState(null);
  //假資料
  const videoList = [];

  return (
    <div className="container">
      <section className="videoListBlock">
        此處是所有的音樂影片
        <div className="videoBlock">
          <iframe
            width="560"
            height="315"
            src="https://www.youtube.com/embed/wjFVPJZvG6c?si=EHTsHYE7opFgLru8"
            title="YouTube video player"
            frameborder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
            allowfullscreen
          ></iframe>
        </div>
      </section>
      {isReading && (
        <section className="videoDetailsBlock">包含歌詞、歌手資訊等</section>
      )}
    </div>
  );
};

export default Song;
