import React, { useState, useEffect, useRef, useCallback } from "react";
import QAService from "../services/QA-service";
import { Link } from "react-router-dom";

const QA = ({ currentUser, setCurrentUser }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState({ title: "", content: "" });
  const [isPosting, setIsPosting] = useState(null);
  const [isReading, setIsReading] = useState(null);
  const [readMessage, setReadMessage] = useState([]);
  const [reply, setReply] = useState(null);
  const [newReply, setNewReply] = useState({ content: "" });
  const linkRef = useRef();
  const [isModifying, setIsModifying] = useState(null);
  const [deleteConfirm, setDeleteConfirm] = useState(false);
  const [ellipsisClick, setellipsisClick] = useState(false);
  //首次render所有留言
  useEffect(() => {
    QAService.getMessages()
      .then((data) => {
        if (data && data.length !== 0) {
          setMessages(data);
        }
      })
      .catch((e) => {
        console.log(e);
      });
  }, []);
  //處理發布留言
  const handlePostMessage = () => {
    QAService.postMessage(newMessage.title, newMessage.content).then((data) => {
      setMessages([...messages, data]);
      setNewMessage({ title: "", content: "" });
      setIsPosting(!isPosting);
    });
  };

  //處理新回覆
  const handlePostReply = (_id) => {
    QAService.postReply(_id, newReply.content)
      .then((data) => {
        setReply(data);
        setNewReply({ content: "" });
      })
      .catch((error) => {
        console.error(error);
      });
  };
  //
  //處理時間顯示
  function timeAgo(dateString) {
    const date = new Date(dateString);
    const now = new Date();
    const secondsPast = (now.getTime() - date.getTime()) / 1000;
    switch (true) {
      case secondsPast < 60:
        return "幾秒前";
      case secondsPast < 3600:
        return parseInt(secondsPast / 60) + " 分鐘";
      case secondsPast <= 86400:
        return parseInt(secondsPast / 3600) + " 小時";
      case secondsPast < 86400 * 7:
        return parseInt(secondsPast / 86400) + " 天";
      default:
        let year = date.getFullYear();
        let month = date.getMonth() + 1;
        let day = date.getDate();
        return year + "年" + month + "月" + day + "日";
    }
  }
  //處理輸入框外觀
  const textareaRef = useRef(null);
  useEffect(() => {
    if (textareaRef.current) {
      textareaRef.current.style.height = "auto";
      textareaRef.current.style.height = `${textareaRef.current.scrollHeight}px`;
    }
  }, [newMessage]);
  useEffect(() => {
    return () => {
      window.addEventListener("popstate", onClose);
      window.history.replaceState(null, "", "/QA");
    };
  }, []);
  //處理點擊留言
  const handleClick = (_id) => {
    QAService.getURLMessage(_id).then((data) => {
      setReadMessage(data);
      setIsReading(true);
      setReply(data.replies);
      window.history.pushState(null, "", "QA/" + data._id);
    });
  };
  //處理QADetails關閉
  const onClose = () => {
    setIsPosting("");
    setIsReading("");
    setReadMessage([]);
    setReply(null);
  };

  //刪除留言處理
  const handleDeleteConfirm = (_id, e) => {
    e.stopPropagation();
    setDeleteConfirm(_id);
  };
  const handleDeleteMessage = useCallback((_id, e) => {
    e.stopPropagation();
    QAService.deleteMessage(_id).then((data) => {
      window.alert(data);
    });
    setDeleteConfirm(false);
  }, []);
  useEffect(() => {
    QAService.getMessages()
      .then((data) => {
        if (data && data.length !== 0) {
          setMessages(data);
        }
      })
      .catch((e) => {
        console.log(e);
      });
  }, [handleDeleteMessage]);
  return (
    <div>
      <section className="QASearch">
        <textarea className="QASearch" placeholder="問題搜尋" />
        <button className="QASearch">
          <i className="fa-solid fa-magnifying-glass"></i>
        </button>
      </section>
      <section className="QABlock">
        <div className="QABorder">
          <div className="QANav">
            <a href="/QA">全部</a>
            <a href="/QA">追蹤</a>
            <a href="/QA">熱門</a>
          </div>
          <hr />
          <div className="post">
            <button onClick={() => setIsPosting(!isPosting)}>
              有什麼想問嗎？ 立即發問<span> </span>
              <i className="fa-solid fa-pen"></i>
            </button>
          </div>
          <div className="postBlock">
            {isPosting && (
              <div className="QAPost">
                <button
                  className="xMark"
                  onClick={() => setIsPosting(!isPosting)}
                >
                  <i className="fa-solid fa-xmark fa-2xl"></i>
                </button>
                <textarea
                  className="title"
                  type="text"
                  placeholder="標題"
                  value={newMessage.title}
                  onChange={(e) =>
                    setNewMessage({ ...newMessage, title: e.target.value })
                  }
                />
                <textarea
                  className="content"
                  type="text"
                  placeholder="內容"
                  value={newMessage.content}
                  onChange={(e) =>
                    setNewMessage({ ...newMessage, content: e.target.value })
                  }
                />
                <button className="post" onClick={handlePostMessage}>
                  發布
                </button>
              </div>
            )}
          </div>
          {isReading && (
            <div className="DetailsBlock">
              <div className="close">
                <Link to={`/QA`}>
                  <button className="xMark" onClick={onClose}>
                    <i className="fa-solid fa-xmark fa-2xl"></i>
                  </button>
                </Link>
              </div>
              <div className="messageSelf">
                <div className="inform">
                  <div className="QAPoster">
                    {readMessage.user.nickname
                      ? readMessage.user.nickname
                      : readMessage.user.username}
                  </div>
                  <div
                    className="QATimer"
                    title={new Date(readMessage.time).toLocaleString("zh-TW", {
                      hour12: true,
                      year: "numeric",
                      month: "numeric",
                      day: "numeric",
                      hour: "numeric",
                      minute: "numeric",
                      weekday: "long",
                    })}
                  >
                    {timeAgo(readMessage.time)}
                  </div>
                </div>
                {currentUser.user._id == readMessage.user._id && (
                  <div className="QAChangeButton">
                    <button>
                      <i class="fa-solid fa-ellipsis-vertical"></i>
                    </button>
                    <button
                      onClick={(e) =>
                        handleDeleteConfirm(readMessage.user._id, e)
                      }
                    >
                      刪除留言
                    </button>
                  </div>
                )}

                {deleteConfirm === readMessage.user._id && (
                  <div className="deleteConfirmBlock">
                    <p>刪除後就不能復原，確定要刪除嗎？</p>
                    <button
                      className="deleteCheck"
                      onClick={(e) =>
                        handleDeleteMessage(readMessage.user._id, e)
                      }
                    >
                      確認刪除
                    </button>
                    <button
                      className="deleteCheck"
                      onClick={(e) => {
                        e.stopPropagation();
                        setDeleteConfirm(false);
                      }}
                    >
                      取消
                    </button>
                  </div>
                )}
                <div className="QATitle">{readMessage.title}</div>
                <div className="QAContent">{readMessage.content}</div>
              </div>
              {reply.length > 0 ? (
                <div className="replyBlock">
                  <p>全部留言</p>
                  <ul>
                    <hr />
                    {reply.map((replies, index) => (
                      <li key={index}>
                        <div className="replyUser">
                          {replies.user.nickname
                            ? replies.user.nickname
                            : replies.user.username}
                        </div>
                        <div className="replyTimer">
                          {timeAgo(replies.time)}
                        </div>
                        <div className="replyContent">{replies.content}</div>
                        <hr />
                      </li>
                    ))}
                  </ul>
                </div>
              ) : (
                <div className="replyBlock">
                  <p>全部留言</p>
                  <ul>
                    <hr />
                    <div>目前沒有留言，趕緊搶頭香</div>
                  </ul>
                </div>
              )}
              <div className="postReply">
                <textarea
                  className="reply"
                  placeholder="留言......"
                  type="text"
                  value={newReply.content}
                  onChange={(e) =>
                    setNewReply({ ...newReply, content: e.target.value })
                  }
                />
                <button onClick={() => handlePostReply(readMessage._id)}>
                  送出
                </button>
              </div>
            </div>
          )}
          <div className="QA">
            <div>
              <ul>
                {messages.map((message, index) => (
                  <li key={index}>
                    <div
                      onClick={() => {
                        handleClick(message._id);
                      }}
                      ref={linkRef}
                    >
                      <div className="inform">
                        <div className="QAPoster">
                          {message.user.nickname
                            ? message.user.nickname
                            : message.user.username}
                        </div>{" "}
                        <div
                          className="QATimer"
                          title={new Date(message.time).toLocaleString(
                            "zh-TW",
                            {
                              hour12: true,
                              year: "numeric",
                              month: "numeric",
                              day: "numeric",
                              hour: "numeric",
                              minute: "numeric",
                              weekday: "long",
                            }
                          )}
                        >
                          {timeAgo(message.time)}
                        </div>
                      </div>
                      <div className="QATitle">{message.title}</div>
                      <div className="QAContent">{message.content}</div>
                      <hr />
                    </div>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default QA;
