import React, { useState } from "react";

import AddTodo from "./AddTodo";

let nextId = 3;
const initialTodos = [
  { id: 0, title: "Task1", done: true },
  { id: 1, title: "Task2", done: true },
  { id: 2, title: "Task3", done: true },
];
const App = () => {
  const [todos, setTodos] = useState(initialTodos);
  function handleAddTodo(title) {
    setTodos([
      ...todos,
      {
        id: nextId++,
        title: title,
        done: false,
      },
    ]);
  }
  return (
    <div>
      <AddTodo />
    </div>
  );
};

export default App;
