import React, { useState, useEffect, useRef } from "react";
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import NavDropdown from "react-bootstrap/NavDropdown";
import { Link } from "react-router-dom";

const Navigation = () => {
  const [isNavbarOpen, setIsNavbarOpen] = useState(false);
  const navbarRef = useRef(null);
  // 新增Navbar點擊事件監聽，用於收回展開的Nav Bar
  useEffect(() => {
    const handleNavbarClick = (event) => {
      // 如果點擊事件發生在Navbar內，不處理，直接返回
      if (navbarRef.current && navbarRef.current.contains(event.target)) {
        return;
      }
      // 點擊Navbar以外的地方，收回展開的Nav Bar
      setIsNavbarOpen(false);
    };

    // 添加Navbar點擊事件監聽
    navbarRef.current.addEventListener("click", handleNavbarClick);

    return () => {
      // 移除Navbar點擊事件監聽
      navbarRef.current.removeEventListener("click", handleNavbarClick);
    };
  }, []);

  return (
    <Navbar expand="lg" ref={navbarRef}>
      <Container style={{ margin: 0, maxWidth: "100vw" }}>
        <Navbar.Brand as={Link} to="/">
          <img
            src="/images/thai-learn-logo.png"
            width="0"
            className="d-inline-block align-bottom"
            style={{ marginRight: "1rem" }}
            alt="Logo"
          />
          PaSaThai GO
        </Navbar.Brand>
        <Navbar.Toggle
          aria-controls="basic-navbar-nav"
          onClick={() => setIsNavbarOpen(!isNavbarOpen)} // 新增按鈕點擊事件，切換展開/收回 Nav Bar
        />
        <Navbar.Collapse
          id="basic-navbar-nav"
          className={`collapse ${isNavbarOpen ? "show" : ""}`} // 透過狀態控制 Nav Bar 的顯示
          ref={navbarRef}
        >
          <Nav className="ms-auto">
            <Nav.Link as={Link} to="/">
              首頁
            </Nav.Link>
            <Nav.Link as={Link} to="/register">
              加入會員
            </Nav.Link>
            <Nav.Link as={Link} to="/login">
              會員登入
            </Nav.Link>
            <Nav.Link as={Link} to="/">
              登出
            </Nav.Link>
            <Nav.Link as={Link} to="QA">
              問答區
            </Nav.Link>
            <NavDropdown title="學習資源" id="basic-nav-dropdown">
              <NavDropdown.Item as={Link} to="resource">
                推薦資源
              </NavDropdown.Item>
              <NavDropdown.Item as={Link} to="alphabet">
                字母表
              </NavDropdown.Item>
              <NavDropdown.Item as={Link} to="song">
                一起來聽歌
              </NavDropdown.Item>
            </NavDropdown>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default Navigation;
