import React, { useRef, useEffect, useState } from "react";
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import NavDropdown from "react-bootstrap/NavDropdown";
import { Link } from "react-router-dom";
import AuthService from "../services/auth-service";
const Navigation = ({ currentUser, setCurrentUser }) => {
  const navbarRef = useRef(null);
  const handleLogout = () => {
    AuthService.logout(); //清空localStorage
    setCurrentUser(null);
  };
  return (
    <Navbar expand="lg">
      <Container style={{ margin: 0, maxWidth: "100vw" }}>
        <Navbar.Brand as={Link} to="/">
          <img
            src="/images/thai-learn-logo.png"
            width="40"
            className="d-inline-block align-bottom"
            style={{ marginRight: "0.5rem", marginLeft: "0.5rem" }}
            alt="Logo"
          />
          PaSaThai GO
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto">
            <Nav.Link as={Link} to="/">
              首頁
            </Nav.Link>
            {!currentUser && (
              <Nav.Link as={Link} to="register">
                加入會員
              </Nav.Link>
            )}
            {!currentUser && (
              <Nav.Link as={Link} to="login">
                會員登入
              </Nav.Link>
            )}
            {currentUser && (
              <Nav.Link as={Link} to="profile">
                個人頁面
              </Nav.Link>
            )}{" "}
            {currentUser && <Nav.Link onClick={handleLogout}>登出</Nav.Link>}
            <Nav.Link as={Link} to="QA">
              問答區
            </Nav.Link>
            <NavDropdown title="學習資源" id="basic-nav-dropdown">
              <NavDropdown.Item as={Link} to="resource">
                推薦資源
              </NavDropdown.Item>
              <NavDropdown.Item as={Link} to="alphabet">
                字母表
              </NavDropdown.Item>
              <NavDropdown.Item as={Link} to="song">
                一起來聽歌
              </NavDropdown.Item>
            </NavDropdown>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default Navigation;
