import { useState, useEffect } from "react";
import profileService from "../services/profile-service";
import { useNavigate } from "react-router-dom";

const Profile = ({ currentUser, setCurrentUser }) => {
  let [nickname, setNickname] = useState("");
  let [isModifying, setIsModifying] = useState(true);
  const navigate = useNavigate();
  useEffect(() => {
    if (currentUser) {
      authService.getUserInfo().then((response) => {
        const userInfo = response.data;
        if (userInfo.nickname) {
          setNickname(userInfo.nickname);
          setIsModifying(true);
        } else {
          setIsModifying(false);
          setNickname("");
        }
      });
    }
  }, [currentUser]);
  const handleTakeToLogin = () => {
    navigate("/login");
  };

  const handleNicknameChange = (e) => {
    setNickname(e.target.value);
  };

  const patchNickname = () => {
    profileService
      .patch({ nickname })
      .then(() => {
        setIsModifying(false);
      })
      .catch((e) => {
        console.log(e.response.data);
      });
  };

  return (
    <div style={{ padding: "3rem" }}>
      {!currentUser && (
        <div>
          <p>在獲取您的個人資料之前，您必須先登錄。</p>
          <button
            className="btn btn-primary btn-lg"
            onClick={handleTakeToLogin}
          >
            點擊此處登入
          </button>
        </div>
      )}
      {currentUser && (
        <div>
          <h2>會員資料：</h2>

          <table className="table">
            <tbody>
              <tr>
                <td>
                  <strong>用戶名稱：{currentUser.user.username}</strong>
                </td>
              </tr>
              <tr>
                <td
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "center",
                  }}
                >
                  <strong>暱稱：</strong>
                  {isModifying ? (
                    <strong>{nickname}</strong>
                  ) : (
                    <input
                      type="text"
                      value={nickname}
                      onChange={handleNicknameChange}
                    />
                  )}
                  <button
                    className="btn btn-primary btn-block"
                    onClick={() => {
                      if (isModifying) {
                        patchNickname();
                      }
                      setIsModifying(!isModifying);
                    }}
                  >
                    {isModifying ? "修改暱稱" : "儲存暱稱"}
                  </button>
                </td>
              </tr>
              <tr>
                <td>
                  <strong>您的電子信箱: {currentUser.user.email}</strong>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Profile;
