import React, { useState, useEffect } from "react";
import QAService from "../services/QA-service";
const QA = () => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");

  useEffect(() => {
    QAService.getMessages()
      .then((data) => {
        if (data && data.length !== 0) {
          setMessages(data);
        }
      })
      .catch((e) => {
        console.log(e);
      });
  }, []);
  const handlePostMessage = () => {
    QAService.postMessage(newMessage).then((data) => {
      setMessages([...messages, data]);
      setNewMessage("");
    });
  };

  function timeAgo(date) {
    const now = new Date();
    const secondsPast = (now.getTime() - date.getTime()) / 1000;
    if (secondsPast < 60) {
      return parseInt(secondsPast) + " 秒前";
    }
    if (secondsPast < 3600) {
      return parseInt(secondsPast / 60) + " 分鐘前";
    }
    if (secondsPast <= 86400) {
      return parseInt(secondsPast / 3600) + " 小時前";
    }
    if (secondsPast > 86400) {
      let day = date.getDate();
      let month = date
        .toDateString()
        .match(/ [a-zA-Z]*/)[0]
        .replace(" ", "");
      let year =
        date.getFullYear() == now.getFullYear() ? "" : " " + date.getFullYear();
      return day + " " + month + year;
    }
  }

  return (
    <div>
      <section style={{ display: "flex", justifyContent: "flex-end" }}>
        <input className="QASearch" type="text" placeholder="問題搜尋" />
        <button className="QASearch">
          <i className="fa-solid fa-magnifying-glass"></i>
        </button>
      </section>
      <section className="QABlock">
        <div className="QABorder">
          <div className="QANav">
            <a href="/QA">全部</a>
            <a href="/QA">追蹤</a>
            <a href="/QA">熱門</a>
          </div>
          <hr />
          <div className="QA">
            <div>
              <input
                type="text"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
              />
              <button onClick={handlePostMessage}>提交</button>{" "}
              <ul>
                {messages.map((message, index) => (
                  <li key={index}>
                    <p className="poster">
                      {message.user.nickname
                        ? message.user.nickname
                        : message.user.username}
                    </p>
                    <p>{message.content}</p>
                    <p>{new Date(message.time).toLocaleString()}</p>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default QA;
