import React, { useEffect, useState } from "react";

const Song = () => {
  const [state, setState] = useState(0);

  useEffect(() => {
    console.log(`此次是第${state}次渲染`);

    return () => {
      console.log(`上一次渲染是第${state}次`);
    };
  });

  return (
    <div>
      <div> Song</div>
      <button
        onClick={() => {
          setState(state + 1);
        }}
      >
        +
      </button>
    </div>
  );
};

export default Song;
