import React, { useState } from "react";

const Song = () => {
  const [isReading, setisReading] = useState(null);
  return (
    <div className="container">
      <section className="videoListBlock">此處是所有的音樂影片</section>
      {isReading && (
        <section className="videoDetailsBlock">包含歌詞、歌手資訊等</section>
      )}
    </div>
  );
};

export default Song;
