import { useEffect, useRef } from "react";

function VideoPlayer({ src, isPlaying }) {
  const ref = useRef(null);
  if (isPlaying) {
    ref.current.play();
  } else {
    ref.current.pause();
  }
  return <video ref={ref} src={src} loop playsInline />;
}

function App() {}
export default App;
