import { useState } from "react";
import profileService from "../services/profile-service";
import { useNavigate } from "react-router-dom";

const Profile = ({ currentUser, setCurrentUser }) => {
  let [nickname, setNickname] = useState("");
  let [isModifying, setIsModifying] = useState(true);
  const navigate = useNavigate();

  const handleTakeToLogin = () => {
    navigate("/login");
  };

  const handleNicknameChange = (e) => {
    setNickname(e.target.value);
  };

  const modifyNickname = () => {
    const userId = currentUser.user._id;
    const newNicknameValue = nickname;
    if (!currentUser.user.nickname) {
      // 如果暱稱不存在，執行 POST 請求
      profileService
        .post(nickname)
        .then(() => {
          setCurrentUser((prevUser) => ({
            ...prevUser,
            user: {
              ...prevUser.user,
              nickname,
            },
          }));
        })
        .catch((error) => {
          console.log(error.response.data);
        });
    } else {
      // 如果暱稱已存在，執行 PATCH 請求
      profileService
        .patch({ userId, newNickname: newNicknameValue })
        .then(() => {
          setCurrentUser((prevUser) => ({
            ...prevUser,
            user: {
              ...prevUser.user,
              nickname: newNicknameValue,
            },
          }));
        })
        .catch((error) => {
          console.log(error.response.data);
        });
    }
  };

  return (
    <div style={{ padding: "3rem" }}>
      {!currentUser && (
        <div>
          <p>在獲取您的個人資料之前，您必須先登錄。</p>
          <button
            className="btn btn-primary btn-lg"
            onClick={handleTakeToLogin}
          >
            點擊此處登入
          </button>
        </div>
      )}
      {currentUser && (
        <div>
          <h2>會員資料：</h2>

          <table className="table">
            <tbody>
              <tr>
                <td>
                  <strong>用戶名稱：{currentUser.user.username}</strong>
                </td>
              </tr>
              <tr>
                <td
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                  }}
                >
                  <strong style={{ flex: 1 }}>暱稱：</strong>
                  {isModifying ? (
                    <strong style={{ flex: 1 }}>{nickname}</strong>
                  ) : (
                    <input
                      type="text"
                      value={nickname}
                      onChange={handleNicknameChange}
                      style={{ flex: 1 }}
                    />
                  )}
                  <button
                    className="btn btn-primary btn-block"
                    style={{ marginLeft: "auto" }}
                    onClick={() => {
                      if (!isModifying) {
                        modifyNickname();
                      }
                      setIsModifying(!isModifying);
                    }}
                  >
                    {isModifying ? "修改暱稱" : "儲存暱稱"}
                  </button>
                </td>
              </tr>
              <tr>
                <td>
                  <strong>您的電子信箱: {currentUser.user.email}</strong>
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Profile;
