export class FadeInAnimation {
  constructor(node) {
    this.node = node;
  }
  //處理動畫時間發生的事
  start(duration) {
    this.duration = duration;
    if (duration === 0) {
      //時間為零的時候，直接結束
      this.onProgerss(1);
    } else {
    }
    //時間不為零就開始跑動畫
  }

  onProgerss(progress) {
    this.node.style.opacity = progress;
  }
}
