import React, { useState } from "react";
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import NavDropdown from "react-bootstrap/NavDropdown";

const HomepageNav = () => {
  // const [isNavOpen, setIsNavOpen] = useState(false);

  // const toggleNav = () => {
  //   setIsNavOpen(!isNavOpen);
  // };

  return (
    <Navbar
      expand="lg"
      style={{
        backgroundColor: "#b2967d",
      }}
    >
      <Container style={{ margin: 0, maxWidth: "100vw" }}>
        <Navbar.Brand href="#home" style={{ color: "rgba(73, 3, 3, 1)" }}>
          <img
            src="/images/thai-learn-logo.png"
            width="30"
            className="d-inline-block align-bottom"
            style={{ marginRight: "1rem" }}
            alt="Logo"
          />
          PaSaThai GO
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto" style={{ textAlign: "right" }}>
            <Nav.Link href="#home">首頁</Nav.Link>
            <Nav.Link href="#qa">問答區</Nav.Link>
            <NavDropdown title="學習資源" id="basic-nav-dropdown">
              <NavDropdown.Item href="#resource">推薦資源</NavDropdown.Item>
              <NavDropdown.Item href="#alphabet">字母表</NavDropdown.Item>
              <NavDropdown.Item href="#song">一起來聽歌</NavDropdown.Item>
            </NavDropdown>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default HomepageNav;
