import { BrowserRouter, Routes, Route } from "react-router-dom";
import Layout from "./components/Layout";
import React, { useState } from "react";
import "./styles/App.css";
import Homepage from "./components/Homepage";
import QA from "./components/QA";
import Resource from "./components/Resource";
import Alphabet from "./components/Alphabet";
import Song from "./components/Song";
import Login from "./components/login";
import Register from "./components/register";
import authService from "./services/auth-service";
import Profile from "./components/Profile";
import ResetPassword from "./components/ResetPassword";
import QADetails from "./components/QADetails";

const App = () => {
  let [currentUser, setCurrentUser] = useState(authService.getCurrentUser);
  //處理QADetails關閉
  const onClose = () => {
    setIsReading("");
    setReadMessage([]);
  };
  //處理時間顯示
  function timeAgo(dateString) {
    const date = new Date(dateString);
    const now = new Date();
    const secondsPast = (now.getTime() - date.getTime()) / 1000;
    switch (true) {
      case secondsPast < 60:
        return "幾秒前";
      case secondsPast < 3600:
        return parseInt(secondsPast / 60) + " 分鐘";
      case secondsPast <= 86400:
        return parseInt(secondsPast / 3600) + " 小時";
      case secondsPast < 86400 * 7:
        return parseInt(secondsPast / 86400) + " 天";
      default:
        let year = date.getFullYear();
        let month = date.getMonth() + 1;
        let day = date.getDate();
        return year + "年" + month + "月" + day + "日";
    }
  }
  const [readMessage, setReadMessage] = useState([]);

  return (
    <BrowserRouter>
      <Routes>
        <Route
          path="/"
          element={
            <Layout currentUser={currentUser} setCurrentUser={setCurrentUser} />
          }
        >
          <Route index element={<Homepage />} />
          <Route path="register" element={<Register />} />
          <Route
            path="login"
            element={
              <Login
                currentUser={currentUser}
                setCurrentUser={setCurrentUser}
              />
            }
          />
          <Route
            path="QA"
            element={<QA />}
            readMessage={readMessage}
            timeAgo={timeAgo}
            onClose={onClose}
          />
          <Route path="QA/:messageId" element={<QADetails />} />
          <Route path="resource" element={<Resource />} />
          <Route path="alphabet" element={<Alphabet />} />
          <Route path="song" element={<Song />} />
          <Route
            path="profile"
            element={
              <Profile
                currentUser={currentUser}
                setCurrentUser={setCurrentUser}
              />
            }
          />
          <Route path="resetpassword" element={<ResetPassword />} />
        </Route>
      </Routes>
    </BrowserRouter>
  );
};

export default App;
